<?global $SKIP_JS_STORES;?><?if(!$SKIP_JS_STORES):?>	<?$SKIP_JS_STORES=true;?>	<script type="text/javascript">	$(document).ready(function() {		var bStores = (arTires2Options["THEME"]["STORES_SOURCE"]!= 'IBLOCK' ? 'Y' : 'N');		$('.stores_text_wrapp a').each(function() {			var href = '';						if (bStores=="Y"){				if((href = $(this).attr('data-storehref'))){					$(this).attr('href', href)				}			}			else{				if((href = $(this).attr('data-iblockhref'))){					$(this).attr('href', href)				}			}		});	});	<?if($arParams["HAS_OFFERS"] != "Y"):?>		var arStoresAddress = [];				for(key in arBasketWithPropsAspro.BASKET){			if('<?=$templateData['ELEMENT_ID'];?>' == arBasketWithPropsAspro.BASKET[key].PRODUCT_ID){				arStoresAddress.push(arBasketWithPropsAspro.BASKET[key].STORES);			}		}		$('.stores_block_wrap .item_wrapper .stores_block').each(function(){			var _this = $(this),				storeTitle = _this.find('.title_stores').text();							if(arStoresAddress.indexOf(storeTitle) != -1){				_this.addClass('active');			}		});	<?endif;?>	$('.stores_wrapp').removeClass('loadings');	if($('.item_wrapper .stores_block').length)	{		$('body').on('change', '.checked_block .stores_block_wrap.ext_block .filter input[type="radio"]:checked', function(){		//$('.active .stores_block_wrap.ext_block .filter input[type="radio"]:checked').change(function(){			var _this = $(this),				objUrl = parseUrlQuery(),				obData = <?=CUtil::PhpToJSObject($templateData, false, true, true)?>,				add_url = "";			$('.js_sync_block').addClass('loadings vertical');			if("clear_cache" in objUrl)			{				if(objUrl.clear_cache == "Y")					add_url += "&clear_cache=Y";			}						if(_this.data('offers') == 'Y')			{				obData = _this.data('params');								var arStoresAddressTmp = [];								$('.checked_block .stores_block_wrap .item_wrapper .stores_block').removeClass('active');								for(key in arBasketWithPropsAspro.BASKET)				{					if(_this.data('element') == arBasketWithPropsAspro.BASKET[key].PRODUCT_ID)					{						arStoresAddressTmp.push(arBasketWithPropsAspro.BASKET[key].STORES);					}				}				$('.checked_block .stores_block_wrap .item_wrapper .stores_block').each(function(){					var _this = $(this),						storeTitle = _this.find('.title_stores').text();											if(arStoresAddressTmp.indexOf(storeTitle) != -1){						_this.addClass('active');					}				});			}			BX.ajax({				url: arTires2Options["SITE_DIR"]+"ajax/showInfoByStore.php?STORE_ID="+_this.val()+'&REGION_ID='+_this.closest('.stores_item').data('val')+add_url,				method: 'POST',				data: obData,				dataType: 'html',				processData: false,				start: true,				headers: [{'name': 'X-Requested-With', 'value': 'XMLHttpRequest'}],				onfailure: function(data) {					console.log(data);					alert('Error connecting server');					$('.js_sync_block').removeClass('loadings vertical');				},				onsuccess: function(html){					var ob = BX.processHTML(html);					// inject					$('.js_sync_block').html(ob.HTML);					setBasketWithPropsStatusBtn(_this.closest('.stores_block').find('.title_stores').text(), _this.val(), _this.data('element'));					$('.js_sync_block').removeClass('loadings vertical');										if(_this.data('offers') != 'Y')					{						if($('#headerfixed .logo-row .but-cell').length)						{							if($('.offers_img.wof').length || $('.prices_tab').length) //show button								buttonHtml = '<span class="more btn btn-default btn-lg">'+BX.message('MORE_INFO_SKU')+'</span>';							else if($('.middle_info .buy_block .button_block').length)								buttonHtml = $('.middle_info .buy_block .button_block').html();							$('#headerfixed .logo-row .but-cell').html('<div class="button_block">'+buttonHtml.replace('btn-lg', '')+'</div>');						}						if($('#headerfixed .logo-row .price-cell').length)						{							if($('.item_main_info *[itemprop="offers"]').length) //show price							{								if($('.offers_img.wof').length || $('.prices_tab').length)								{									if($('.prices_block .price').length)										priceHtml = $('.prices_block .cost.prices').html().replace('id', 'data-id');								}								else								{									if($('.prices_block .with_matrix').length)										priceHtml = '<div class="with_matrix">'+$('.prices_block .with_matrix').html()+'</div>';									else if($('.prices_block .price_group.min').length)										priceHtml = $('.prices_block .price_group.min').html();									else if($('.prices_block .price_matrix_wrapper').length)										priceHtml = $('.prices_block .price_matrix_wrapper').html();									else										priceHtml = '';								}							}							$('#headerfixed .logo-row .price-cell').html('<div class="cost prices">'+priceHtml+'</div>');						}					}					var itemStockHtml = ($('.middle_info .item-stock').length ? $('.middle_info .item-stock').html() : '')					$('.module_products_list .item-stock').html(itemStockHtml);				}			})		})		<?if($arParams["HAS_OFFERS"] != "Y"):?>			$('.stores_block_wrap.ext_block .filter input[type="radio"]').trigger('change');		<?endif;?>	}		</script><?endif;?>